import { MediaCard, Frame,FormLayout, Layout, Page, AppProvider } from '@shopify/polaris';
import React, {Component} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';
import image from './connector.png';

class Main extends Component{

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Frame>
                    <Page>
                        <MediaCard>
                              <img alt="" width="640px" height="360px" style={{
                                objectPosition: 'center'
                              }} src={image} />
                            <Button onClick={event =>  location.href="/xero_connect"} primary>Save Settings</Button>
                        </MediaCard>
                    </Page>
                </Frame>
            </AppProvider>
        )
    }

}

export default Main;

const wrapper = document.getElementById("xero_connect");
wrapper ? ReactDOM.render(<Main />, wrapper) : false;
