import { Thumbnail, Button,MediaCard, Frame,FormLayout, Layout, Page, AppProvider } from '@shopify/polaris';
import React, {Component} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';
import image from './connector.png';

class Main extends Component{

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Frame>
                    <Page>
                        <div>
                        <img src={image} alt="Connect to Xero" width="640px" height="360px" />
                        <Button style={{float:'left', paddingLeft:'20px'}} onClick={event =>  window.top.location.href="/xero_connect"} primary>Save Settings</Button>
                        </div>
                        <div>

                        </div>
                    </Page>
                </Frame>
            </AppProvider>
        )
    }
}

export default Main;

const wrapper = document.getElementById("xero_connect");
wrapper ? ReactDOM.render(<Main />, wrapper) : false;
