import {DataTable, DatePicker, Checkbox ,Select, AppProvider, Button, Card,TextField, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';

function DatePickerExample() {
  const [{month, year}, setDate] = useState({month: new Date().getMonth(), year: new Date().getFullYear()});
  const [selectedDates, setSelectedDates] = useState({
    start: new Date(),
    end: new Date(),
  });
  const handleMonthChange = useCallback(
    (month, year) => setDate({month, year}),
    [],
  );
  return (
    <DatePicker
      month={month}
      year={year}
      onChange={setSelectedDates}
      onMonthChange={handleMonthChange}
      selected={selectedDates}
      multiMonth
      allowRange
    />
  );
}

function ExportForm(){
    return(
        <Form>
            <DatePickerExample />
            <br />
            <Button primary>Export Data</Button>
        </Form>
    )
}

function AutoSyncCheckbox(props) {
    // const label =
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  return (
    <Checkbox
      label= "Auto Sync"
      checked={checked}
      onChange={handleChange}
    />
  );
}

function AccountSelect(props) {
  const [selected, setSelected] = useState('200');

  const handleSelectChange = useCallback((value) => setSelected(value), []);

  const options = [
    {label: '200 - Sale', value: '200'},
    {label: '201 - VAT', value: '201'},
    {label: '901 - Bank', value: '901'},
  ];

  return (
    <Select
      label= {props.label}
      options={options}
      onChange={handleSelectChange}
      value={selected}
    />
  );
}

function AccountSettingForm(){
    return (
        <Form>
            <AccountSelect label="Sales Account"/>
            <AccountSelect label="Shipping Account"/>
            <AccountSelect label="Payment Account"/>
            <AutoSyncCheckbox />
            <br />
            <Button primary>Save Settings</Button>
        </Form>
    )
}

function DataTableExample() {
  const rows = [
    ['Emerald Silk Gown', '$875.00', 124689, 140],
    ['Mauve Cashmere Scarf', '$230.00', 124533, 83],
  ];
  return (
    <Page>
