import {DataTable, DatePicker, Checkbox ,Select, AppProvider, Card,TextField, Button, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';
const AppConfig = window.config;
console.log("window.config.shopify_store.sale_account: "+ window.config.shopify_store.sale_account)

function DatePickerExample() {
  const [{month, year}, setDate] = useState({month: new Date().getMonth(), year: new Date().getFullYear()});
  const [selectedDates, setSelectedDates] = useState({
    start: new Date(),
    end: new Date(),
  });
  const handleMonthChange = useCallback(
    (month, year) => setDate({month, year}),
    [],
  );
  return (
    <DatePicker
      month={month}
      year={year}
      onChange={setSelectedDates}
      onMonthChange={handleMonthChange}
      selected={selectedDates}
      multiMonth
      allowRange
    />
  );
}

function ExportForm(){
    return(
        <Form>
            <DatePickerExample />
            <br />
            <Button primary>Export Data</Button>
        </Form>
    )
}

function AutoSyncCheckbox(props) {

  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  return (
    <Checkbox
      label= "Automatically Sync (at midnight everyday)"
      checked={checked}
      onChange={handleChange}
    />
  );
}

function AccountSelect(props) {

    const [selected, setSelected] = useState(props.current_account);
    const handleSelectChange = useCallback((value) => setSelected(value), []);

    const options = props.accounts.map((account) =>
        ({label: account.name, value: account.code})
    )

    return (
    <Select
        label= {props.label}
        options={options}
        onChange={handleSelectChange}
        value={selected}
    />
    );
}

const data = [{"name":"123","code":12321},
