            {"name":"dsadsa213","code":5823},]

function OptionList(props){
        console.log(data)
        options = data.map((account) =>
        ({label: account.name, value: account.code})
        )
        return options
}


class AccountSettingForm extends Component{
    constructor(props) {
        super(props);
        this.state = {
            current_sale_account : AppConfig.shopify_store.sale_account,
            current_shipping_account : AppConfig.shopify_store.shipping_account,
            current_payment_account : AppConfig.shopify_store.payment_account,
            auto_sync : AppConfig.shopify_store.auto_sync,
            sale_accounts : AppConfig.sale_accounts,
            shipping_accounts : AppConfig.shipping_accounts,
            payment_accounts : AppConfig.payment_accounts,
        };
        console.log(AppConfig.sale_accounts)
    }

    SaleAccountOptions(){
        return <OptionList accounts={data} />
    }
    ShippingAccountOptions(){
        return <OptionList accounts={data}/>
    }
    PaymentAccountOptions(){
        return <OptionList accounts={data}/>
    }

    handleSelectChange(){
        alert('change')
    }

    handleSubmit(){
        alert('submit')
    }
    render() {
        return (
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <Select label="Sales Account" options={this.SaleAccountOptions} onChange={this.handleSelectChange}
                            value={this.state.current_sale_account}/>
                    <Select label="Shipping Account" options={this.ShippingAccountOptions}
                            onChange={this.handleSelectChange} value={this.state.current_shipping_account}/>
                    <Select label="Payment Account" options={this.PaymentAccountOptions}
                            onChange={this.handleSelectChange} value={this.state.current_payment_account}/>
                    <AutoSyncCheckbox/>
                    <br/>
                    <Button submit>Save Settings</Button>
                </FormLayout>
            </Form>
        )
    }
}

function DataTableExample() {
  const rows = [
    ['Emerald Silk Gown', '$875.00', 124689, 140],
    ['Mauve Cashmere Scarf', '$230.00', 124533, 83],
  ];
  return (
    <Page>
      <Card>
        <Card.Section>
        <p>History log of sync history, including automated jobs.</p>
        <p> Orders synced this month: 55 </p>
        <p>Current Orders per plan: 100</p>
        </Card.Section>
        <DataTable
          columnContentTypes={[
            'datetime',
            'datetime',
            'text',
            'text',
          ]}
          headings={[
            'Excution Time',
            'Finish Time',
            'Status',
            'Message',
          ]}
          rows={rows}
        />
      </Card>
    </Page>
  );
}

function DisconnectForm(){
    return(
        <Card >
            <Card.Section>
                <p>Organization Name: </p>
                <p >Status: <span style={{color:'green'}}><b>Connected</b></span></p>
                <Button destructive>Disconnect From Xero</Button>
            </Card.Section>
        </Card>
    )
}

function PlanForm(){
    return(
        <Layout >
            <Layout.Section oneThird>
                <div className="card">
                    <div className="card-body d-flex flex-column">
                        <Card title="Plan name1"
                          sectioned>
                        <p><b>Free</b></p>
                        <p>Sync Customers, Products, Orders to Xero</p>
                        <p>Manually Sync in date range</p>
                        <p>Automatic Updates every 24 hours</p>
                        <p>Account Mapping</p>
                        <p>Synchronization History</p>
                        <p>100 Orders/month</p>
                    </Card>
                    </div>
                </div>
            </Layout.Section>
            <Layout.Section oneThird>
                <div className="card">
                    <div className="card-body d-flex flex-column">
                        <Card title="Plan name2"
                              sectioned>
                            <Form>
                                <p><b>$19.99/month</b></p>
                                <p>All Essential Features</p>
                                <p>Automatic Updates every 12 hours</p>
                                <p>Sync Gift Cards, Refunds to Xero</p>
                                <p>800 Orders/month</p>
                                <div style={{textAlign:'center', marginTop:'10px'}}>
                                    <Button primary>Sign Up</Button>

                                </div>
                            </Form>
                        </Card>
                    </div>
                </div>

            </Layout.Section>
            <Layout.Section oneThird>
                <div className="card">
                    <div className="card-body d-flex flex-column">
                        <Card title="Plan name3"
                        sectioned>
                        <Form>
                            <p><b>$29.99/month</b></p>
                            <p>All Standard Features</p>
                            <p>Automatic Updates every 3 hours</p>
                            <p>Unlimited Orders per month</p>
                            <div style={{textAlign:'center', marginTop:'10px'}}>
                                <Button primary>Sign Up</Button>
                            </div>
                        </Form>
                    </Card>
                    </div>
                </div>
            </Layout.Section>
        </Layout>
    )
}

class Hello extends Component{

    constructor(props) {
        super(props);
        this.state = {
            config: AppConfig,
        }
    }
    render() {
        return (
            <AppProvider i18n={translations}>
                <Page title="General Settings">
                  <Layout>
                    <Layout.AnnotatedSection
                    description={
                        <p>
                          Please choose your account accordingly: <br />
                        - Sales Account will be applied to Invoice Line Items' Account<br />
                        - Shipping Account will be applied to Shipping as an Invoice Line Item<br />
                        - Payments will go to Payment Account on Xero
                        </p>
                    }
                        >
                        <AccountSettingForm />
                    </Layout.AnnotatedSection>
                  </Layout>
                </Page>
                <Page title="Export To Xero">
                  <Layout>
                    <Layout.AnnotatedSection
                    description={
                        <p>
                        - Choose date from and date to and export your data to Xero.<br />
                        - Customers will be synced to Xero as: Shopify - "Customer Name" - ("Customer Id")<br />
                        - Invoices will be synced to Xero as: Shopify - "Order Id"<br />
                        - Products will be synced to Xero as Xero Items
                        </p>
                    }>
                        <ExportForm />
                    </Layout.AnnotatedSection>
                      </Layout>
                </Page>
                <Page title="History">
                  <Layout>
                      <Layout.Section>
                        <DataTableExample />
                    </Layout.Section>
                  </Layout>
                </Page>
                <Page title="Plans">
                    <PlanForm />
                </Page>
                <Page title="Disconnect From Xero">
                    <DisconnectForm />
                </Page>

            </AppProvider>
        )
    }

}

export default Hello;

const wrapper = document.getElementById("container12");
wrapper ? ReactDOM.render(<Hello />, wrapper) : false;
