import { Thumbnail, Button,MediaCard, Frame,FormLayout, Layout, Page, AppProvider } from '@shopify/polaris';
import React, {Component} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';
// import image from './connector.png';

class Main extends Component{

    constructor(props) {
        super(props);
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Frame>
                    <Page>
                        <MediaCard primaryAction={{
                            content: 'Connect To Xero',
                            onAction: () => {window.top.location.href="/xero_connect"}
                            }} portrait={true}>
                            <img alt="" width="100%" height="100%" style={{
                            objectFit: 'cover',
                            objectPosition: 'center'
                            }} src="https://burst.shopifycdn.com/photos/smiling-businesswoman-in-office.jpg?width=1850" />
                        </MediaCard>
                        <Button onClick={event =>  window.top.location.href="/xero_connect"} primary>Save Settings</Button>
                        <Thumbnail
                          source="https://burst.shopifycdn.com/photos/smiling-businesswoman-in-office.jpg?width=1850"
                          alt="Black choker necklace"
                          style={{
                            width: '100%',
                            height: '100%'
                            }}
                        />
                        <img src="https://burst.shopifycdn.com/photos/smiling-businesswoman-in-office.jpg?width=1850" alt="Girl in a jacket" width="500" height="600" />
                    </Page>
                </Frame>
            </AppProvider>
        )
    }
}

export default Main;

