import {Checkbox ,Select, AppProvider, Button, Card,TextField, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';

function AutoSyncCheckbox(props) {
    // const label =
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  return (
    <Checkbox
      label= "Auto Sync"
      checked={checked}
      onChange={handleChange}
    />
  );
}

function AccountSelect(props) {
  const [selected, setSelected] = useState('200');

  const handleSelectChange = useCallback((value) => setSelected(value), []);

  const options = [
    {label: '200 - Sale', value: '200'},
    {label: '201 - VAT', value: '201'},
    {label: '901 - Bank', value: '901'},
  ];

  return (
    <Select
      label= {props.label}
      options={options}
      onChange={handleSelectChange}
      value={selected}
    />
  );
}

function AccountSettingForm(){
    return (
        <Form>
            <AccountSelect label="Sales Account"/>
            <AccountSelect label="Shipping Account"/>
            <AccountSelect label="Payment Account"/>
            <AutoSyncCheckbox />
            <br />
            <Button primary>Save Settings</Button>
        </Form>
    )
}

class SettingButton extends Component{
    render(){
        return(
            <Form >
                <Button submit>Setting</Button>
            </Form>
        )
    }
}

class NameForm extends Component{

    constructor(props) {
        super(props);
        this.state = {name: ''};
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleNameChange(event){
        this.setState({name: event.target.value});
    }

    handleSubmit(event){
        alert("Name: " + this.state.name)
        event.preventDefault();
    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <TextField
                    value={this.state.name}
                    onChange={this.handleNameChange}
                    label="Name"
                    helpText={<span>Polaris</span>}
                    />
                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        )
    }
}

function NameFormExample(){
    const [name, setName] = useState('');

    const handleSubmit = useCallback((_event) => {
    alert(" Name: "+ name)
    }, [name]);

    const handleNameChange = useCallback((value) => setName(value), []);

    return (
    <Form onSubmit={handleSubmit}>
      <FormLayout>
        <TextField
          value={name}
          onChange={handleNameChange}
          label="Name"
          type="text"
          helpText={
            <span>
              Polaris
            </span>
          }
        />

        <Button submit>Submit</Button>
      </FormLayout>
    </Form>
  );

}



class Hello extends Component {

    constructor(props){
        super(props);
        this.state = {
            message:'Welcome to code 101'
        };
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Page title="Shopify Xero">
                  <Layout>
                    <Layout.AnnotatedSection
                      title="General Settings">
                        <AccountSettingForm />
                    </Layout.AnnotatedSection>
                    <Layout.AnnotatedSection
                      title="Export To Xero">
                        <NameFormExample />
                    </Layout.AnnotatedSection>
                      <Layout.AnnotatedSection
                      title="History">
                        <NameFormExample />
                    </Layout.AnnotatedSection>
                      <Layout.AnnotatedSection
                      title="Plans">
                        <NameFormExample />
                    </Layout.AnnotatedSection>
                      <Layout.AnnotatedSection
                      title="Disconnect From Xero">
                        <NameFormExample />
                    </Layout.AnnotatedSection>
                  </Layout>
                </Page>
            </AppProvider>
        )
    }

}

const Index = () => (
    <Hello />
);

export default Hello;

