import {Button, Card,TextField, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
// import { Alert } from 'reactstrap';


class SettingButton extends Component{
    render(){
        return(
            <Form >
                <Button submit>Setting</Button>
            </Form>
        )
    }
}

class NameForm extends Component{

    constructor(props) {
        super(props);
        this.state = {name: ''};
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleNameChange(event){
        this.setState({name: event.target.value});
    }

    handleSubmit(event){
        alert("Name: " + this.state.name)
        event.preventDefault();
    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <TextField
                    value={this.state.name}
                    onChange={this.handleNameChange}
                    label="Name"
                    helpText={<span>Polaris</span>}
                    />
                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        )
    }
}

function NameFormExample(){
    const [name, setName] = useState('');

    const handleSubmit = useCallback((_event) => {
    setName('');
    }, []);



}

class Hello extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            message:'Welcome to code 101'
        };
    }

    render() {
        return (
            <Page>
                <Layout>
                    <Layout.Section oneThird>
                        <SettingButton />
                    </Layout.Section>
                    <Layout.Section oneThird>
                        <NameForm />
                    </Layout.Section>
                </Layout>
            </Page>
        )
    }

}

const Index = () => (
    <Hello />
);

export default Index;
