import {DataTable, DatePicker, Checkbox ,Select, AppProvider, Button, Card,TextField, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';

function DatePickerExample() {
  const [{month, year}, setDate] = useState({month: 1, year: 2018});
  const [selectedDates, setSelectedDates] = useState({
    start: new Date('Wed Feb 07 2018 00:00:00 GMT-0500 (EST)'),
    end: new Date('Mon Mar 12 2018 00:00:00 GMT-0500 (EST)'),
  });
  const handleMonthChange = useCallback(
    (month, year) => setDate({month, year}),
    [],
  );
  return (
    <DatePicker
      month={month}
      year={year}
      onChange={setSelectedDates}
      onMonthChange={handleMonthChange}
      selected={selectedDates}
      multiMonth
      allowRange
    />
  );
}

function ExportForm(){
    return(
        <Form>
            <DatePickerExample />
            <br />
            <Button primary>Export Data</Button>
        </Form>
    )
}

function AutoSyncCheckbox(props) {
    // const label =
  const [checked, setChecked] = useState(false);
  const handleChange = useCallback((newChecked) => setChecked(newChecked), []);

  return (
    <Checkbox
      label= "Auto Sync"
      checked={checked}
      onChange={handleChange}
    />
  );
}

function AccountSelect(props) {
  const [selected, setSelected] = useState('200');

  const handleSelectChange = useCallback((value) => setSelected(value), []);

  const options = [
    {label: '200 - Sale', value: '200'},
    {label: '201 - VAT', value: '201'},
    {label: '901 - Bank', value: '901'},
  ];

  return (
    <Select
      label= {props.label}
      options={options}
      onChange={handleSelectChange}
      value={selected}
    />
  );
}

function AccountSettingForm(){
    return (
        <Form>
            <AccountSelect label="Sales Account"/>
            <AccountSelect label="Shipping Account"/>
            <AccountSelect label="Payment Account"/>
            <AutoSyncCheckbox />
            <br />
            <Button primary>Save Settings</Button>
        </Form>
    )
}

function DataTableExample() {
  const rows = [
    ['Emerald Silk Gown', '$875.00', 124689, 140],
    ['Mauve Cashmere Scarf', '$230.00', 124533, 83],
  ];
  return (
    <Page>
      <Card>
        <Card.Section>
        <p>History log of sync history, including automated jobs.</p>
        <p> Orders synced this month: 55 </p>
        <p>Current Orders per plan: 100</p>
        </Card.Section>
        <DataTable
          columnContentTypes={[
            'datetime',
            'datetime',
            'text',
            'text',
          ]}
          headings={[
            'Excution Time',
            'Finish Time',
            'Status',
            'Message',
          ]}
          rows={rows}
        />
      </Card>
    </Page>
  );
}

class NameForm extends Component{

    constructor(props) {
        super(props);
        this.state = {name: ''};
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleNameChange(event){
        this.setState({name: event.target.value});
    }

    handleSubmit(event){
        alert("Name: " + this.state.name)
        event.preventDefault();
    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <TextField
                    value={this.state.name}
                    onChange={this.handleNameChange}
                    label="Name"
                    helpText={<span>Polaris</span>}
                    />
                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        )
    }
}

function NameFormExample(){
    const [name, setName] = useState('');

    const handleSubmit = useCallback((_event) => {
    alert(" Name: "+ name)
    }, [name]);

    const handleNameChange = useCallback((value) => setName(value), []);

    return (
    <Form onSubmit={handleSubmit}>
      <FormLayout>
        <TextField
          value={name}
          onChange={handleNameChange}
          label="Name"
          type="text"
          helpText={
            <span>
              Polaris
            </span>
          }
        />

        <Button submit>Submit</Button>
      </FormLayout>
    </Form>
  );

}

function PlanForm(){
    return(
        <Form>
            <Layout>
                <Layout.Section oneThird>
                    <Card title="Florida"></Card>
                </Layout.Section>
                <Layout.Section oneThird>
                    <Card title="Florida2"></Card>
                </Layout.Section>
                <Layout.Section oneThird>
                    <Card title="Florida3"></Card>
                </Layout.Section>
            </Layout>
        </Form>
    )
}



class Hello extends Component {

    constructor(props){
        super(props);
        this.state = {
            message:'Welcome to code 101'
        };
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Page title="General Settings">
                  <Layout>
                    <Layout.Section
                      >
                        <AccountSettingForm />
                    </Layout.Section>
                  </Layout>
                </Page>
                <Page title="Export To Xero">
                  <Layout>
                    <Layout.AnnotatedSection>
                        <ExportForm />
                    </Layout.AnnotatedSection>
                      </Layout>
                </Page>
                <Page title="History">
                  <Layout>
                      <Layout.AnnotatedSection>
                        <DataTableExample />
                    </Layout.AnnotatedSection>
                  </Layout>
                </Page>
                <Page title="Plans">
                    <PlanForm />
                </Page>
                <Page title="Disconnect From Xero">
                    <Layout>
                        <Layout.AnnotatedSection>
                            <NameFormExample />
                        </Layout.AnnotatedSection>
                    </Layout>
                </Page>
            </AppProvider>
        )
    }

}

const Index = () => (
    <Hello />
);

export default Hello;

// const wrapper = document.getElementById("container12");
