import {AppProvider, Button, Card,TextField, TextStyle, EmptyState, Form, FormLayout, Layout, Page } from '@shopify/polaris';
import React, {Component, useState, useCallback} from 'react';
import ReactDOM from "react-dom";
import '@shopify/polaris/dist/styles.css';
import translations from '@shopify/polaris/locales/en.json';


class SettingButton extends Component{
    render(){
        return(
            <Form >
                <Button submit>Setting</Button>
            </Form>
        )
    }
}

class NameForm extends Component{

    constructor(props) {
        super(props);
        this.state = {name: ''};
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handleSubmit = this.handleSubmit.bind(this)
    }

    handleNameChange(event){
        this.setState({name: event.target.value});
    }

    handleSubmit(event){
        alert("Name: " + this.state.name)
        event.preventDefault();
    }

    render(){
        return(
            <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                    <TextField
                    value={this.state.name}
                    onChange={this.handleNameChange}
                    label="Name"
                    helpText={<span>Polaris</span>}
                    />
                    <Button submit>Submit</Button>
                </FormLayout>
            </Form>
        )
    }
}

function NameFormExample(){
    const [name, setName] = useState('');

    const handleSubmit = useCallback((_event) => {
    alert(" Name: "+ name)
    }, [name]);

    const handleNameChange = useCallback((value) => setName(value), []);

    return (
    <Form onSubmit={handleSubmit}>
      <FormLayout>
        <TextField
          value={name}
          onChange={handleNameChange}
          label="Name"
          type="text"
          helpText={
            <span>
              Polaris
            </span>
          }
        />

        <Button submit>Submit</Button>
      </FormLayout>
    </Form>
  );

}



class Hello extends Component {

    constructor(props){
        super(props);
        this.state = {
            message:'Welcome to code 101'
        };
    }

    render() {
        return (
            <AppProvider i18n={translations}>
                <Page title="Details page">
                  <Layout>
                    <Layout.Section>Page-level banners </Layout.Section>
                    <Layout.Section> Primary content </Layout.Section>
                    <Layout.Section secondary> Secondary content </Layout.Section>
                    <Layout.Section> Page actions (delete and save) </Layout.Section>
                  </Layout>
                </Page>
                <Page title="Annotated layout">
                  <Layout>
                    <Layout.AnnotatedSection
                      title="Store details"
                      description="Shopify and your customers will use this information to contact you."
                    >
                      {/* Details fields */}
                    </Layout.AnnotatedSection>
                    <Layout.AnnotatedSection
                      title="Store address"
                      description="This address will appear on your invoices."
                    >
                      {/* Address fields */}
                    </Layout.AnnotatedSection>
                  </Layout>
                </Page>
            </AppProvider>
        )
    }

}

const Index = () => (
    <Hello />
);

export default Hello;

